SELECT ool.order_id, oo.order_no, oo.create_time, oo.client_id, oo.client_name, oo.client_mobile, oo.enterprise_id, oo.store_id, oo.order_status, oo.pay_status, oo.dest_number, oo.use_number, oo.sub_order_number, oo.order_amount, oo.actual_amount, oo.pay_type, oo.pay_no, oo.pay_time, ool.enterprise_name, ool.remarks, ool.store_remarks, ool.store_name, oso.product_id, oso.product_img, oso.product_number, oso.specification_id, oso.sub_order_id, oso.unit_price, oso.total_price, osol.product_name, osol.specification_name FROM ord_order oo JOIN ord_order_language ool ON ool.order_id = oo.order_id AND ool.language = #{language,jdbcType=VARCHAR} AND ool.is_deleted = 0 JOIN ord_sub_order oso ON oso.order_id = oo.order_id AND oso.is_deleted = 0 JOIN ord_sub_order_language osol ON osol.sub_order_id = oso.sub_order_id AND osol.language = #{language,jdbcType=VARCHAR} AND osol.is_deleted = 0 WHERE oo.is_deleted = 0
and oo.client_id = #{clientId,jdbcType=VARCHAR}
and oo.store_id = #{storeId,jdbcType=VARCHAR}
and oo.enterpriseId = #{enterpriseId,jdbcType=VARCHAR}
and oo.order_status = #{orderStatus,jdbcType=VARCHAR}
and oo.pay_status = #{payStatus,jdbcType=VARCHAR}
and oo.order_id = #{orderId,jdbcType=VARCHAR}
and oo.client_mobile = #{clientMobile,jdbcType=VARCHAR}
and oo.channel_type = #{channelType,jdbcType=VARCHAR}
and oo.create_time >= #{orderTimeStart}
and oo.create_time < #{orderTimeEnd}
order by create_time desc limit #{startNumber},#{endNumber}

select count(order_id) from ord_order oo
where oo.is_deleted = 0

    and oo.client_id = #{clientId,jdbcType=VARCHAR}


    and oo.store_id = #{storeId,jdbcType=VARCHAR}


    and oo.enterpriseId = #{enterpriseId,jdbcType=VARCHAR}


    and oo.order_status = #{orderStatus,jdbcType=VARCHAR}


    and oo.pay_status = #{payStatus,jdbcType=VARCHAR}


    and oo.order_id = #{orderId,jdbcType=VARCHAR}


    and oo.client_mobile = #{clientMobile,jdbcType=VARCHAR}


    and oo.channel_type = #{channelType,jdbcType=VARCHAR}


